import './checkout-item.scss';

import React from 'react';
import { connect } from 'react-redux';
import { clearItemFromCart, increaseQuantity } from '../../redux/cart/cart.action';


function CheckOutItem({ cartItem, removeItemFromCart, increase }) {
    
    const {imageUrl, name, quantity, price} = cartItem;

    return (
        <div className='checkout-item'>
            <div className="image-container">
                <img src={imageUrl} alt=""/>
            </div>
            <span className="name">{name}</span>
            <div className="quantity">
                <span className="decrement">&#10094;</span>
                <span className="number">{quantity}</span>
                <span className="increment" onClick={() => increase(item)} >&#10095;</span>
            </div>
            
            <span className="price">{price}</span>
            <div className="remove-button" onClick={() => removeItemFromCart(cartItem)}> &#10005; </div>
               
        </div>
    )
}

const mapDispatchToPorps = dispatch => ({
    removeItemFromCart: (item) => dispatch(clearItemFromCart(item)),
    increase: (item) => dispatch(increaseQuantity(item))
});

export default connect(null, mapDispatchToPorps)(CheckOutItem);
