import './collection-page.scss';

import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectCollections, selectCollection } from '../../redux/shop/shop.selectors';
import CollectionItem from '../../component/collection-item/collection-item.component'

function CollectionPage({ match: { params: { collectionId } }, collection }) {
    const collectionItems = collection.items;
    
    return (
        <div className='collection-page'>
            <h2 className='title'>{collectionId}</h2>
            <div className="collection">
                {
                collectionItems.map((item) => <CollectionItem key={item.id} item={item} /> )
            }
            </div>
            
            
        </div>
    )
}

// const mapStateToProps = createStructuredSelector({
//     collections: selectCollections
// })

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
})

export default connect(mapStateToProps)(CollectionPage);
