import styled from 'styled-components';

export const CollectionItemContainer = styled.div`
  width: 22vw;  
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;

  &-image {
    width: 100%;
    height: 95%;
    background-size: cover;
    background-position: center;
    margin-bottom: 5px;
  }

  &-footer {
    width: 100%;
    height: 5%;
    display: flex;
    justify-content: space-between;
    font-size: 18px;
    margin-bottom: 15px;

    .name {
      width: 90%;
    }
    .price {
      width: 10%;
    }
  }

  .custom-button {
    width: 80%;
    opacity: 0.7;
    position: absolute;
    top: 250px;
    display: none;
  }

  &:hover {
    .collection-item-image{
      opacity: 0.8;
    }

    .custom-button {
      display: flex;
      opacity: 0.85;
    }
  }
`;
