import { takeLatest, put, all, call } from 'redux-saga/effects';

import UserActionTypes from './user.types';
// import { signInWithGoogle } from '../../firebase/firebase.utils'
import { googleProvider, auth, createUserProfileDocument } from '../../firebase/firebase.utils'

import {
    signInSuccess,
    signInfailure
} from './user.action';

export function* signInWithGoogle() {
    try {
        const { user } = yield auth.signInWithPopup(googleProvider);
        // console.log('user', user);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        // console.log('user: ', {
        //     id: userSnapshot.id, 
        //     ...userSnapshot.data()
        //  });
        yield put(signInSuccess({
            id: userSnapshot.id,
            ...userSnapshot.data()
        }));

    } catch (error) {
        console.error(error);
        yield put(signInfailure(error));
    }
};

// what we want to do: listen to GOOGLE_SIGN_IN_START and trigger our actual sign from the saga.
export function* onGoogleSignInStart() {
    yield takeLatest(
        UserActionTypes.GOOGLE_SIGN_IN_START,
        signInWithGoogle
    );
};

// here input is an action object
export function* signInWithEmail({payload: {email, password}}) {
    
    console.log('email: ', email);
    console.log('password: ', password);

    try {
        const { user } = yield auth.signInWithEmailAndPassword(email, password);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        console.log('user: ', {
            id: userSnapshot.id,
            ...userSnapshot.data()
        });

        yield put(signInSuccess({
            id: userSnapshot.id,
            ...userSnapshot.data()
        }));

    } catch (error) {
        console.log('error: ', error.message);
        yield put(signInfailure(error));
    }


}

export function* onEmailSignInStart() {
    yield takeLatest(
        UserActionTypes.EMAIL_SIGN_IN_START,
        signInWithEmail   
    )
}

export function* userSagas() {
    yield all([
        call(onGoogleSignInStart),
        call(onEmailSignInStart)
    ]);
};
